INSERT INTO `wp_posts` VALUES("152", "1", "2016-04-08 09:16:30", "2016-04-08 15:16:30", "<div class=\"jumbotron jumbotron-top elearnign-bg jumbo-about jumbo-elearning jumbo-pro jumbo-sectors\">\r\n  <div class=\"container\">\r\n    <h1 class=\"white-text m-b-none\">E-learning</h1>\r\n  </div>\r\n</div>\r\n\r\n<div id=\"trigger\" class=\"spacer s0 trigger-about\"></div>\r\n\r\n<div class=\"container m-t-md m-b-md intro-servicios\">\r\n  <!-- row of columns -->\r\n  <div class=\"row m-b-lg\">\r\n    <div class=\"col-xs-12 col-xs-offset-0 col-sm-12 col-sm-offset-0 col-md-10 col-md-offset-1 col-lg-8 col-lg-offset-2\">\r\n      <section>\r\n        <header>\r\n          <h2 class=\"Normal400 m-b-lg\">\r\n            <!-- <small class=\"m-l-xxs gray-light-text sub\">E-learning</small><br/> -->\r\n            Experiencias digitales para el Conocimiento\r\n          </h2>\r\n        </header>\r\n        <div class=\"row m-lg\">\r\n          <div class=\"col-xs-12 col-xs-offset-0 col-sm-12 col-sm-offset-0 col-md-10 col-md-offset-1 col-lg-8 col-lg-offset-2\">\r\n            <blockquote class=\"text-md text-left\">\r\n              <p class=\"blockquote-text\">Innovamos en tecnologías educativas para crear experiencias de aprendizaje inolvidables</p>\r\n            </blockquote>\r\n          </div>\r\n        </div>\r\n        <div class=\"clearfix\"></div>\r\n        <p class=\"medium\">La información y el conocimiento a través de la tecnología, son la vía  para potencializar las capacidades individuales y colectivas; impulsamos el desarrollo de las organizaciones, los procesos y las personas a través de experiencias digitales para la transformación.</p>\r\n      </section>\r\n    </div>\r\n  </div>\r\n</div>\r\n\r\n<!--*********************\r\nAPRENDIZAJE INTERACTIVO\r\n*********************-->\r\n<div class=\"full-width\">\r\n  <div class=\"row-height\">\r\n  <div class=\"col-sm-6 col-sm-height col-sm-middle p-none half-img-bg elearnign-bg-img-1 col-sm-push-6\"></div>\r\n\r