INSERT INTO `wp_posts` VALUES("234", "1", "2016-04-11 11:26:20", "2016-04-11 17:26:20", "<div class=\"jumbotron jumbotron-top startups-bg jumbo-about jumbo-sectors\">\r\n  <div class=\"container\">\r\n    <h1 class=\"white-text m-b-none\">Startups</h1>\r\n  </div>\r\n</div>\r\n\r\n<div id=\"trigger\" class=\"spacer s0 trigger-about\"></div>\r\n\r\n<div class=\"container text-center\">\r\n  <h2 class=\"m-b-none serv-tit\">Innovation is the base for Startups competitiveness</h2>\r\n</div>\r\n\r\n<div class=\"full-width\">\r\n  <div class=\"row-height\">\r\n    <div class=\"col-sm-6 col-sm-height col-sm-middle p-none half-img-bg startups-bg-img-1\"></div>\r\n    <div class=\"col-sm-6 col-sm-height col-sm-top p-none half-p-t-container\">\r\n      <div class=\"inside\">\r\n        <div class=\"half-r-container\">\r\n          <section class=\"mgs-ux-l p-l-lg cont-g\">\r\n            <p class=\"lead ux-desc m-t-n-sm\">Many market tendencies point out that an organization\'s  direction and the digital economy is being defining by four key technologies: <em>Cloud Computing, Social media, Big Data and Mobility.</em> </p>\r\n\r\n            <p>Startups are characterized for opting for digital and disruptive projects to be competitive in the market, although nine out of ten fail in their initial years. The limited financial resources and the knowledge gap in strategic technological  key issues and business are the biggest challenges for the sector. </p>\r\n          </section>\r\n        </div><!-- /half-container -->\r\n      </div>\r\n    </div>\r\n  </div>\r\n</div>\r\n\r\n<div class=\"container desc-c\">\r\n  <div class=\"col-sm-12 col-sm-offset-0 col-md-10 col-md-offset-1\">\r\n    <section>\r\n      <header>\r\n        <h3 class=\"Medium500 m-b-lg serv-tit\">Technological innovation</h3>\r\n      </header>\r\n      <div class=\"row\">\r\n        <div class=\"col-sm-6 desc\">\r\n          <p class=\"lead ux-desc m-t-n-sm\">Through a deep technological knowledge, creativity and innovation, in Metagraphos we support  Startups in achieving their projects. From consolidating their business plan, accompanying, consulting in raising funds, to select the technology that best fit their innovation project. </p>\r\n        </div>\r\n        <div class=\"col-sm-6 desc\">\r\n          <p class=\"lead ux-desc m-t-n-sm\">In <b>metagraphos</b> we innovate to transform knowledge, productivity and your organization’s technology through friendly and intuitive digital experiences.  We combine design thinking, innovation and knowledge to bring your organization to the digital era.</p>\r\n        </div>\r\n      </div>\r\n    </section>\r\n  </div>\r\n</div>\r\n\r\n<div class=\"container\">\r\n  <div class=\"col-sm-12 p-none\">\r\n    <img class=\"img-responsive full-width\" src=\"http://localhost:8888/mgs-blog/wp-content/uploads/2016/04/startups_img_2.jpg\" alt=\"\" />\r\n  </div>\r\n</div>\r\n\r\n<div class=\"container other-services \">\r\n  <!-- row of columns -->\r\n  <div class=\"row\">\r\n    <div class=\"col-sm-12\">\r\n      <section>\r\n        <header>\r\n          <h3 class=\"Medium500 m-b-lg\">Sectors</h3>\r\n        </header>\r\n        <div class=\"team otros-sectores\">\r\n          <div class=\"member-container figure-overlay col-sm-6 p-none\">\r\n            <a href=\"corporate\">\r\n            <figure class=\"member\">\r\n              <img class=\"img-responsive full-width\" src=\"http://localhost:8888/mgs-blog/wp-content/uploads/2016/04/fm-corporativo.jpg\"\r\n                   alt=\"Corporate\" />\r\n              \r\n              <figcaption class=\"white-text b-l\">\r\n                <!-- <p class=\"medium light300 titulo\">Cinco variables una soloa gráfica</p> -->\r\n                <p class=\"text-xl m-b-n-sm descripcion\">Corporate</p>\r\n              </figcaption>\r\n\r\n              <blockquote class=\"no-quotes light300 medium p-md\">\r\n                <svg fill=\"#ffffff\" height=\"20\" viewBox=\"0 0 20 20\" width=\"20\" xmlns=\"http://www.w3.org/2000/svg\">\r\n                    <path d=\"M0 0h24v24H0z\" fill=\"none\"/>\r\n                    <path d=\"M12 4l-1.41 1.41L16.17 11H4v2h12.17l-5.58 5.59L12 20l8-8z\"/>\r\n                </svg>\r\n                <span>View information</span>\r\n\r\n            </figure>\r\n            </a>\r\n          </div>\r\n\r\n          <div class=\"member-container figure-overlay col-sm-6 p-none\">\r\n            <a href=\"government\">\r\n            <figure class=\"member\">\r\n              <img class=\"img-responsive full-width\" src=\"http://localhost:8888/mgs-blog/wp-content/uploads/2016/04/fm-gobierno.jpg\"\r\n                   alt=\"Ernesto Riestra CTO / Fundadora\" />\r\n              \r\n              <figcaption class=\"white-text b-l\">\r\n                <!-- <p class=\"medium light300 titulo\">Cinco variables una soloa gráfica</p> -->\r\n                <p class=\"text-xl m-b-n-sm descripcion\">Government</p>\r\n              </figcaption>\r\n\r\n              <blockquote class=\"no-quotes light300 medium p-md\">\r\n                <svg fill=\"#ffffff\" height=\"20\" viewBox=\"0 0 20 20\" width=\"20\" xmlns=\"http://www.w3.org/2000/svg\">\r\n                    <path d=\"M0 0h24v24H0z\" fill=\"none\"/>\r\n                    <path d=\"M12 4l-1.41 1.41L16.17 11H4v2h12.17l-5.58 5.59L12 20l8-8z\"/>\r\n                </svg>\r\n                <span>View information</span>\r\n\r\n            </figure>\r\n            </a>\r\n          </div>\r\n        </div>\r\n      </section>\r\n    </div>\r\n  </div>\r\n</div> <!-- /container -->", "Startups", "", "publish", "closed", "closed", "", "startups-en", "", "", "2016-04-11 13:04:19", "2016-04-11 19:04:19", "", "0", "http://localhost:8888/mgs-blog/?page_id=234", "0", "page", "", "0");
